/*
  Solution1O(N),O(N)
  1. while  length
  2. length array
  3. for  array 
 */
class Solution1 {
    fun reversePrint(head: ListNode?): IntArray {
        var temp = head
        var length = 0
        //1. while  length
        while (temp != null) {
            length++
            temp = temp.next
        }
        //2. length array
        val array = IntArray(length)
        temp = head//temp
        //3. for  array 
        for (i in 0 until length) {
            array[length - 1 - i] = temp?.`val` ?: 0
            temp = temp?.next
        }
        return array
    }
}
