class Solution3 {
    var tmp = ArrayList<Int>()
    fun reversePrint(head: ListNode?): IntArray {
        recur(head)
        // tmp  res 
        val res = IntArray(tmp.size)
        for (i in res.indices) res[i] = tmp[i]
        return res
    }
    fun recur(head: ListNode?) {
    //1.  head.next  head == null
    if (head == null) return
    recur(head.next)
    //2. tmp.add(head.val)
    tmp.add(head.`val`)
	}
