private static final int POOL_SIZE = 4;
private static final FindState[] FIND_STATE_POOL = new FindState[POOL_SIZE];

private List<SubscriberMethod> getMethodsAndRelease(FindState findState) {
    List<SubscriberMethod> subscriberMethods = new ArrayList<>(findState.subscriberMethods);
    findState.recycle(); //findState 
    synchronized (FIND_STATE_POOL) {
        for (int i = 0; i < POOL_SIZE; i++) {
            if (FIND_STATE_POOL[i] == null) {
                // findState  new
                FIND_STATE_POOL[i] = findState;
                break;
            }
        }
    }
    return subscriberMethods;
}

//
private FindState prepareFindState() {
    //
    synchronized (FIND_STATE_POOL) { //FindState[] FIND_STATE_POOL
        for (int i = 0; i < POOL_SIZE; i++) { //POOL_SIZE = 4
            FindState state = FIND_STATE_POOL[i];
            if (state != null) {
                FIND_STATE_POOL[i] = null; //null
                return state; // FindState
            }
        }
    }
    return new FindState(); 
}

static class FindState {
    final List<SubscriberMethod> subscriberMethods = new ArrayList<>();
    final Map<Class, Object> anyMethodByEventType = new HashMap<>();
    final Map<String, Class> subscriberClassByMethodKey = new HashMap<>();
    final StringBuilder methodKeyBuilder = new StringBuilder(128);
    Class<?> subscriberClass;
    Class<?> clazz;
    boolean skipSuperClasses;
    SubscriberInfo subscriberInfo;

    void initForSubscriber(Class<?> subscriberClass) {
        this.subscriberClass = clazz = subscriberClass;
        skipSuperClasses = false;
        subscriberInfo = null;
    }

    void recycle() {
        subscriberMethods.clear();
        anyMethodByEventType.clear();
        subscriberClassByMethodKey.clear();
        methodKeyBuilder.setLength(0);
        subscriberClass = null;
        clazz = null;
        skipSuperClasses = false;
        subscriberInfo = null;
    }

    // 
    boolean checkAdd(Method method, Class<?> eventType) {
        //2 
        // 
        //methodeventType,methodsubscriberMethods
        //MethodSignaturemethodevent()
        Object existing = anyMethodByEventType.put(eventType, method);
        if (existing == null) { //
            //anyMethodByEventType<eventType, method>
            // existingeventType
            // eventType
            return true;
        } else {//
            if (existing instanceof Method) {
                //eventType
                // anyMethodByEventTypeeventTypeMethod(this)
                if (!checkAddWithMethodSignature((Method) existing, eventType)) {
                    // Paranoia check
                    throw new IllegalStateException();
                }
                // Put any non-Method object to "consume" the existing Method
                // Method  Method
                anyMethodByEventType.put(eventType, this);
            }
            return checkAddWithMethodSignature(method, eventType);
        }
    }

    //eventTypeeventTypekey
    // method.getName() + ">" + eventType.getName()methodKey
    // subscriberClassByMethodKey<methodKey, methodClass>
    private boolean checkAddWithMethodSignature(Method method, Class<?> eventType) {
        methodKeyBuilder.setLength(0);
        methodKeyBuilder.append(method.getName());
        methodKeyBuilder.append('>').append(eventType.getName());
        //onEvent>TestEvent
        String methodKey = methodKeyBuilder.toString();
        //getDeclaringClass:  Class 
        Class<?> methodClass = method.getDeclaringClass();
        //map["onEvent>TestEvent"]=
        //methodClassOldmethodClassmethodClassOld
        // <methodKey, methodClass>
        // @Subscribe
        // 
        // 
        Class<?> methodClassOld = subscriberClassByMethodKey.put(methodKey, methodClass);
        //ClassClass
        if (methodClassOld == null || methodClassOld.isAssignableFrom(methodClass)) {
            // Only add if not already found in a sub class
            //
            return true;
        } else {
            // Revert the put, old class is further down the class hierarchy
            //
            subscriberClassByMethodKey.put(methodKey, methodClassOld);
            return false;
        }
    }

    void moveToSuperclass() {
        if (skipSuperClasses) { //getMethod 
            clazz = null;
        } else {
            clazz = clazz.getSuperclass();
            String clazzName = clazz.getName();
            // Skip system classes, this degrades performance.
            // Also we might avoid some ClassNotFoundException (see FAQ for background).
            if (clazzName.startsWith("java.") || clazzName.startsWith("javax.") ||
                    clazzName.startsWith("android.") || clazzName.startsWith("androidx.")) {
                clazz = null;
            }
        }
    }
}
