private void findUsingReflectionInSingleClass(FindState findState) {
    Method[] methods;
    //getDeclaredMethods  NoClassDefFoundError
    try {
        //getDeclaredMethods  getMethods  Activities 
        methods = findState.clazz.getDeclaredMethods();
    } catch (Throwable th) {
        //getMethods  NoClassDefFoundError getMethods  catch
        try {
            methods = findState.clazz.getMethods();
        } catch (LinkageError error) { // super class of NoClassDefFoundError to be a bit more broad...
            throw new EventBusException(msg, error);
        }
        //clazz.getDeclaredMethods()clazz
        // clazz.getMethods()clazz()
        // skipSuperClassestrue
        findState.skipSuperClasses = true;
    }

    //
    for (Method method : methods) {
        int modifiers = method.getModifiers(); //
        //MODIFIERS_IGNORE = Modifier.ABSTRACT | Modifier.STATIC | BRIDGE | SYNTHETIC;
        //must be public, non-static, and non-abstract"
        if ((modifiers & Modifier.PUBLIC) != 0 && (modifiers & MODIFIERS_IGNORE) == 0) {
            Class<?>[] parameterTypes = method.getParameterTypes();//
            if (parameterTypes.length == 1) {//
                Subscribe subscribeAnnotation = method.getAnnotation(Subscribe.class);
                if (subscribeAnnotation != null) {
                    // Subscribe
                    Class<?> eventType = parameterTypes[0];// 
                    //findState.subscriberMethodsmethod
                    //
                    if (findState.checkAdd(method, eventType)) {
                        ThreadMode threadMode = subscribeAnnotation.threadMode();
                        // SubscriberMethod
                        findState.subscriberMethods.add(new SubscriberMethod(method, eventType, threadMode,
                                subscribeAnnotation.priority(), subscribeAnnotation.sticky()));
                    }
                }
            } else if (strictMethodVerification && method.isAnnotationPresent(Subscribe.class)) {
                String methodName = method.getDeclaringClass().getName() + "." + method.getName();
                throw new EventBusException("@Subscribe method " + methodName +
                        "must have exactly 1 parameter but has " + parameterTypes.length);
            }
        } else if (strictMethodVerification && method.isAnnotationPresent(Subscribe.class)) {
            String methodName = method.getDeclaringClass().getName() + "." + method.getName();
            throw new EventBusException(methodName +
                    " is a illegal @Subscribe method: must be public, non-static, and non-abstract");
        }
    }
}
